package com.zgw.company.base.core.http.interceptorimport com.chenenyu.router.RouteInterceptorimport com.chenenyu.router.RouteResponseimport com.chenenyu.router.annotation.Interceptorimport com.zgw.company.base.Rimport com.zgw.company.base.core.config.Configimport com.zgw.company.base.core.config.IActivityManagerimport com.zgw.company.base.core.ext.routerWithAnimimport com.zgw.company.base.core.http.User@Interceptor(Config.LOGIN_INTERCEPTOR)class RouterLoginInterceptor : RouteInterceptor {    override fun intercept(chain: RouteInterceptor.Chain?): RouteResponse =        if (User.currentUser.isLogin()) {            chain!!.process()        } else {            routerWithAnim(Config.ROUTER_LOGIN_ACTIVITY).anim(R.anim.activity_open,android.R.anim.fade_out).go(                IActivityManager.lastActivity())            chain!!.intercept()    //true表示拦截        }}