import android.view.LayoutInflaterimport androidx.fragment.app.Fragmentimport androidx.fragment.app.FragmentActivityimport com.zgw.company.base.Rimport com.zgw.company.base.dialog.BaseDialogFragmentimport com.zgw.company.base.dialog.DialogBuilderimport com.zgw.company.base.core.ext.clickWithTriggerimport kotlinx.android.synthetic.main.dialog_cancle_follow.view.*class CancelFollowDialog : BaseDialogFragment() {  companion object {    fun create(context: FragmentActivity) =      DialogBuilder(context, CancelFollowDialog::class.java)        .setCancelableOnTouchOutside(false)        .setAnimStyle(R.style.AnimScaleCenter)        .showAllowingStateLoss()    fun create(fragment: Fragment) =      DialogBuilder(fragment.activity!!, CancelFollowDialog::class.java)        .setCancelableOnTouchOutside(false)        .setTargetFragment(fragment,1000)        .setAnimStyle(R.style.AnimScaleCenter)        .showAllowingStateLoss()  }  override fun build(initialBuilder: Builder): Builder {    val view = LayoutInflater.from(activity).inflate(R.layout.dialog_cancle_follow, null)    view.tv_cancel.clickWithTrigger {      cancelListener?.onCancelled(1)      dismiss()    }    view.tv_confirm.clickWithTrigger {      positiveListener?.onPositiveButtonClicked(1)      dismiss()    }    return initialBuilder.setView(view)  }}