package com.zgw.company.base.dialogimport android.view.LayoutInflaterimport androidx.fragment.app.FragmentActivityimport com.zgw.company.base.Rimport com.zgw.company.base.core.config.Configimport com.zgw.company.base.core.ext.clickWithTriggerimport com.zgw.company.base.core.ext.routerWithAnimimport kotlinx.android.synthetic.main.dialog_login.view.*class LoginDialog : BaseDialogFragment() { companion object {  fun create(context: FragmentActivity) =   DialogBuilder(context, LoginDialog::class.java)    .setCancelableOnTouchOutside(false)    .setAnimStyle(R.style.AnimScaleCenter)    .showAllowingStateLoss() } override fun build(initialBuilder: Builder): Builder {  val view = LayoutInflater.from(activity).inflate(R.layout.dialog_login, null)  view.iv_cancle.clickWithTrigger {   cancelListener?.onCancelled(1)   dismiss()  }  view.btn_login.clickWithTrigger {   routerWithAnim(Config.ROUTER_LOGIN_ACTIVITY).anim(R.anim.activity_open,android.R.anim.fade_out).go(this)   dismiss()  }  return initialBuilder.setView(view) }}