package com.zgw.company.base.webviewimport android.app.Activityimport android.content.Intentimport android.os.Bundleimport android.view.Viewimport com.just.agentweb.AgentWebConfigimport com.zgw.company.base.core.http.Userclass JSAgentWebFragment : AgentWebFragment() { private var androidInterface: AndroidInterface? = null override fun onViewCreated(view: View, savedInstanceState: Bundle?) {  super.onViewCreated(view, savedInstanceState)  init() } private fun init() {  if (mAgentWeb != null) {   androidInterface = AndroidInterface(mAgentWeb!!, context!!, this, model)   mAgentWeb?.jsInterfaceHolder?.addJavaObject(model.injectedName.value, androidInterface)  }  AgentWebConfig.syncCookie(mUrl, "mw-token=${User.currentUser.accessToken};Expries=${System.currentTimeMillis()}") } /**  * h5跳登录之后,回来再reload页面  */ override fun onActivityResult(requestCode: Int, resultCode: Int, data: Intent?) {  if (requestCode == 0 && resultCode == Activity.RESULT_OK) {   mAgentWeb?.urlLoader?.reload()  } } override fun onDestroy() {  androidInterface?.onDestroy()  super.onDestroy() } companion object {  fun getInstance(bundle: Bundle?): JSAgentWebFragment {   val mJSAgentWebFragment = JSAgentWebFragment()   if (bundle != null)    mJSAgentWebFragment.arguments = bundle   return mJSAgentWebFragment  } }}